import React from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
// import Items from "./components/items";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [
        {
          id: 1,
          title: "стул серый",
          desc: "Сделан из высококачественного дерева с мягкой тканевой обивкой.",
          category: "chairs",
          price: "50",
        },
        {
          id: 2,
          title: "кресло голубое",
          desc: "Мягкое кресло с широкой спинкой, обитое приятной на ощупь тканью. Сделано из прочного дерева и выдерживает до 150 кг. Идеально подходит для гостиной или спальни.",
          category: "armchairs",
          price: "120",
        },
        {
          id: 3,
          title: "диван трёхместный бежевый",
          desc: "Уютный диван с удобными подлокотниками и съёмными подушками. Обивка выполнена из высококачественной микрофибры, устойчива к загрязнениям. Размеры: 210x90x80 см.",
          category: "sofas",
          price: "300",
        },
        {
          id: 4,
          title: "обеденный стол белый",
          desc: "Круглый обеденный стол диаметром 120 см. Выполнен из влагостойкого МДФ и металла. Лаконичный дизайн подходит для современной кухни или столовой.",
          category: "tables",
          price: "200",
        },
        {
          id: 5,
          title: "комод деревянный",
          desc: "Просторный комод с четырьмя выдвижными ящиками. Изготовлен из натурального дуба с матовым покрытием. Отлично подойдёт для хранения вещей в спальне.",
          category: "storage",
          price: "250",
        },
        {
          id: 6,
          title: "ковёр серый 200x300",
          desc: "Мягкий ковёр из гипоаллергенного материала. Приятная текстура, которая подойдёт для любой комнаты. Легко чистится и устойчив к выцветанию.",
          category: "rugs",
          price: "100",
        },
      ],
    };
  }
  render() {
    return (
      <div className="wrapper">
        <Header />
        {/* <Items items={this.state.items} /> */}
        <Footer />
      </div>
    );
  }
}

export default App;
