import React, { useState, useRef, useEffect } from "react";
import { FaShoppingCart } from "react-icons/fa";
import Order from "./Order";

export default function Header(props) {
  let [setOpen, setFunctionOpen] = useState(false);
  const ref = useRef();

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (ref.current && !ref.current.contains(event.target)) {
        setFunctionOpen(false); // Закрыть корзину при клике вне области
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Обработчик добавления товара в корзину
  const addToOrder = (item) => {
    const existingItem = props.orders.find(
      (orderItem) => orderItem.id === item.id
    );
    if (existingItem) {
      // Если товар уже есть в корзине, увеличиваем количество
      existingItem.quantity += 1;
    } else {
      // Если товара нет, добавляем его с количеством 1
      item.quantity = 1;
      props.orders.push(item);
    }
    // Обновляем состояние корзины
    props.setOrders([...props.orders]);
  };

  return (
    <header>
      <div ref={ref}>
        <span className="Logo">House Staff</span>
        <div>
          <ul className="Menu">
            <li>Про нас</li>
            <li>Контакты</li>
            <li>Кабинет</li>
          </ul>
        </div>
        <FaShoppingCart
          onClick={() => setFunctionOpen(!setOpen)}
          className={`shop-cart-button ${setOpen && "active"}`}
        />
        {setOpen && (
          <div className="testClass">
            {props.orders.length === 0 ? (
              <p>Товаров в корзине нет</p>
            ) : (
              props.orders.map((el) => (
                <div key={el.id}>
                  <Order item={el} />
                  <span>Количество: {el.quantity}</span>
                </div>
              ))
            )}
          </div>
        )}
      </div>
      <div className="Presentation"> </div>
    </header>
  );
}
