import React, { Component } from "react";

export class Categories extends Component {
  constructor(props) {
    super(props);
    this.state = {
      categories: [
        {
          key: "all",
          name: "Все",
        },
        {
          key: "chairs",
          name: "Стулья",
        },
        {
          key: "sofa",
          name: "Диван",
        },
        {
          key: "seats",
          name: "Кресло",
        },
        {
          key: "tables",
          name: "Столы",
        },
      ],
    };
  }
  render() {
    return (
      <div className="categories">
        {this.state.categories.map((el) => (
          <div key={el.key} onClick={() => this.props.chooseCategory(key)}>
            {el.name}
          </div>
        ))}
      </div>
    );
  }
}

export default Categories;
